import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
const b = [];
const i = [];
const n = [];
const g = [];
const o = [];
export class AppComponent {
  title = 'BingoAngular';
  number = 0;
  letter = ''

  newNumber() {
    let validNumber = false
    let numberSelected = 0;
    let letterSelected = 'B';
    do {
      numberSelected = Math.round(Math.random() * 15) + 1;
      let selectLetter = this.number % 15;
      switch (selectLetter) {
        case 0:
          let searchNumber = this.b.find(i => i == numberSelected);
          validNumber = searchNumber === undefined ? false : searchNumber;
          if (!validNumber) {
            this.b.push(numberSelected);
          }
          letterSelected = 'B';
          break;

        default:
          break;
      }
    } while (validNumber);

    this.number = numberSelected;
    this.letter = letterSelected;
    console.log(JSON.stringify(this.b));

  }
}
